mapper.select:
SELECT      EMAIL
FROM        tb_s020.tb_s020_member010
WHERE       EMAIL = #{email}

WITH /* parameter */ PARAM AS ( SELECT #{orgId} AS ORG_ID FROM DUAL ),
/* 단체테이블 tb_s020_org010 */ ORG AS ( SELECT A.ORG_ID, A.ORG_NAME, A.ZIP_CODE, A.ADDRESS, A.DETAIL_ADDRESS, A.TEL_NO FROM tb_s020_org010 A, PARAM WHERE A.ORG_ID = PARAM.ORG_ID ),
/* 단체테이블 tb_s020_org010 join with 회원테이블 tb_s020_member010(대표자 정보 조회)*/ JOIN_WITH_MEMBER AS ( SELECT A.*, B.MEMBER_ID, B.MEMBER_NAME, B.EMAIL, B.HP_NO, B.MEMBER_TP FROM ORG A, tb_s020_member010 B WHERE A.ORG_ID = B.ORG_ID AND B.MEMBER_TP = 'R' )
SELECT * FROM JOIN_WITH_MEMBER
